import DocumentReference = FirebaseFirestore.DocumentReference
import DocumentData = FirebaseFirestore.DocumentData
import SetOptions = FirebaseFirestore.SetOptions
import UpdateData = FirebaseFirestore.UpdateData
import Precondition = FirebaseFirestore.Precondition
import DocumentSnapshot = FirebaseFirestore.DocumentSnapshot
import WriteResult = FirebaseFirestore.WriteResult
import FieldPath = FirebaseFirestore.FieldPath
import { FieldValue } from "@google-cloud/firestore"

export const get = (doc: DocumentReference) => () => doc.get();
export const set = (doc: DocumentReference) => (data: DocumentData, options?: SetOptions) => doc.set(data, options);
export const create = (doc: DocumentReference) => (data: DocumentData) => doc.create(data);
// data: UpdateData, precondition?: Precondition | undefined

export const update = (doc: DocumentReference) => (data:UpdateData|string|FieldPath, precondition?: Precondition, ...args: any[]) => {
    if(typeof data === 'object')
        return doc.update(data);
    return doc.update(data, precondition, ...args);
}

const remove = (doc: DocumentReference) => (precondition?: Precondition) => doc.delete(precondition);
export {remove as delete}; // we are not to write delete otherwise

export const collection = (doc: DocumentReference) => (collectionPath: string) => doc.collection(collectionPath);
export const listCollections = (doc: DocumentReference) => () => doc.listCollections();
export const isEqual = (doc: DocumentReference) => (other: DocumentReference) => doc.isEqual(other);
export const onSnapshot = (doc: DocumentReference) => (onNext: (snapshot: DocumentSnapshot) => void, onError?: (error: Error) => void) => doc.onSnapshot(onNext, onError);
export const incrementField = (doc: DocumentReference) => (field = 'count', n = 1) =>
    doc.set(
        {
            [field]: FieldValue.increment(n)
        },
        { merge: true }
    )
export const id = (doc: DocumentReference) => doc.id;
export const firestore = (doc: DocumentReference) => doc.firestore;
export const parent = (doc: DocumentReference) => doc.parent;
export const path = (doc: DocumentReference) => doc.path;


export default get;
