import * as doc from "./doc"
import * as collection from "./collection"
import * as file from "./file"
import * as admin from "./admin"
import * as storage from "./storage"
import * as firestore from "./firestore"
import * as bucket from "./bucket"
import { applySpec, compose, curry } from 'ramda';
import { FileOptions } from 'firebase-admin/node_modules/@google-cloud/storage';


// These types are optional and are copy pasted from inferred source
// They do help to keep type definitions away from the javascript.
// Todo: remove or simplify these
type BinarySpec = (fileSpec: file.FileSpec) => file.FileSpec & file.FileSpec['createReadStream']
type DataSpec = (docSpec: doc.DocSpec) => doc.DocSpec & doc.DocSpec['getData']
type DocSpec = (collectionSpec: collection.CollectionSpec) => ((documentPath: string) => doc.DocSpec['getData'] & doc.DocSpec) & collection.CollectionSpec
type CollectionSpec = (firestoreSpec: firestore.FirestoreSpec) => ((collectionPath: string) => ((documentPath: string) => doc.DocSpec['getData'] & doc.DocSpec) & collection.CollectionSpec) & firestore.FirestoreSpec
type FirestoreSpec = (adminSpec: admin.AdminSpec) => (name?: string) => ((collectionPath: string) => ((documentPath: string) => doc.DocSpec['getData'] & doc.DocSpec) & collection.CollectionSpec) & firestore.FirestoreSpec
type FileSpec = (bucketSpec: bucket.BucketSpec) => ((name: string, options?: FileOptions) => file.FileSpec & file.FileSpec['createReadStream']) & bucket.BucketSpec
type BucketSpec = (storageSpec: storage.StorageSpec) => ((name?: string) => ((name: string, options?: FileOptions) => file.FileSpec & file.FileSpec['createReadStream']) & bucket.BucketSpec) & storage.StorageSpec
type StorageSpec = (adminSpec: admin.AdminSpec) => () => ((name?: string) => ((name: string, options?: FileOptions) => file.FileSpec & file.FileSpec['createReadStream']) & bucket.BucketSpec) & storage.StorageSpec

// We create a function for each imported spec
// which will allow us to add dependencies to all exported functions
// within a file one by one.
const Doc = curry(applySpec(doc))
const Collection = curry(applySpec(collection))
const Bucket = curry(applySpec(bucket))
const Storage = curry(applySpec(storage))
const File = curry(applySpec(file))
const Firestore = curry(applySpec(firestore))
const Admin = curry(applySpec(admin))
// Now we want to add dependencies in the order they'll likely be given.
// That is
// admin -> firestore -> collection -> document -> data
// or
// admin -> storage -> bucket -> file -> readStream
// However, these are happy paths
// We also want to be able to do
// admin -> firestore -> collection -> where -> data
// or
// admin -> storage -> bucket -> getFiles -> array
// We will make the happy path entirely functional
// that is function after function call can be used
// to specify the needed parameter to go from the first
// dependency (admin) to the last (data)

// this is how a default function for a file is specified
// it attaches all functions of ./doc to the getData function with Object.assign
// this function can than be called, but can also be used to call other methods in ./doc 
const DataSpec: DataSpec = docSpec => Object.assign(
    docSpec.getData, // the default directly returns the get-snapshot data method
    docSpec // Attach the spec
)

// We will repeat this for each parameter untill we only have to pass the `firebase-admin` instance
const DocSpec: DocSpec = (collectionSpec) => Object.assign(
    compose(
        DataSpec,  // return the above function (DataSpec) with the newly created spec
        Doc, // create a new spec for Doc
        collectionSpec.doc // the dependency for the new Doc spec
    ),
    collectionSpec // attach the whole spec to the main function
)

// again, but now to get the collection, which is a depency of doc
const CollectionSpec: CollectionSpec = (firestoreSpec) => Object.assign(
    compose(
        DocSpec, // return the above function (DocSpec) with the newly created spec
        Collection, // create a new spec for Collection
        firestoreSpec.collection // the dependency for the new Collection spec
    ),
    firestoreSpec // attach the whole spec to the main function
)

// we will also do this for files. Instead of the firestore database, these
// are dependent on different things, but the code will be the same.
// First the default function for files. 
const BinarySpec: BinarySpec = (fileSpec) => Object.assign(
    fileSpec.createReadStream, // I'm in favor of readStream, so that's the default
    fileSpec
)

// and for a file, which is a depency of readStream
const FileSpec: FileSpec = (bucketSpec) => Object.assign(
    compose(
        BinarySpec,
        File,
        bucketSpec.file
    ),
    bucketSpec
)

// and for bucket, which is a depency of file
const BucketSpec: BucketSpec = (storageSpec) => Object.assign(
    compose(
        FileSpec,
        Bucket,
        storageSpec.bucket
    ),
    storageSpec
)
// we can breath easy for our next set of depencencies are in
// still waters
// Our functions needn't be so complex anymore,
// we just need firestore
const FirestoreSpec: FirestoreSpec = (adminSpec) => compose(
    CollectionSpec,
    Firestore,
    adminSpec.firestore
)
// and storage
const StorageSpec: StorageSpec = (adminSpec) => compose(
    BucketSpec,
    Storage,
    adminSpec.storage
)
// finally, we can createa a spec for our admin.
export const AdminSpec = compose(
    (adminSpec: admin.AdminSpec) => ({
        firestore: FirestoreSpec(adminSpec),
        storage: StorageSpec(adminSpec)
    }),
    Admin
)
/**
 * And that's all folks. Now try:
 * 
 * import admin = require("firebase-admin");
 * const {firestore, storage} = models(admin)
 * const collection = firestore();
 * const defaultBucket = storage()();
 * const statsDoc = collection('stats')
 * 
 * Functional firebase locked and loaded
 */
export default AdminSpec;
